import { useState, useEffect } from 'react';
import { Dropdown, DropdownButton, Table } from 'react-bootstrap';
import axios from 'axios';

export default () => {

    const [songs, setSongs] = useState();
    const [artists, setArtists] = useState();
    const [genres, setGenres] = useState();


    useEffect( () => {
            async function fetchData() {
                //console.log("API URL: " + process.env.REACT_APP_API_URL);
                let data = await axios.get(process.env.REACT_APP_API_URL);
                //console.log("TOP SONGS: " + data.topSongs);
                setSongs(data.data.topSongs);
                setArtists(data.data.topArtists);
                setGenres(data.data.topGenres);
            }
            fetchData();
        },
    []);

    return(
        <>
          <div id='main-page-background'>
            <div id='dropdown-wrapper'>
              <DropdownButton className='dropdown' variant='secondary' size='lg' title='Category'>
                <Dropdown.Item>
                Songs
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item>
                Artists
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item>
                Genres
                </Dropdown.Item>
              </DropdownButton>
              <DropdownButton className='dropdown' variant='secondary' size='lg' title='Time period'>
                  <Dropdown.Item>
                  Last month
                  </Dropdown.Item>
                  <Dropdown.Divider />
                  <Dropdown.Item>
                  Last 6 months
                  </Dropdown.Item>
                  <Dropdown.Divider />
                  <Dropdown.Item>
                  All time
                  </Dropdown.Item>
              </DropdownButton>
            </div>
            {songs != null && 
                <Table variant='dark' className='table' striped bordered>
                  <tr>
                    <th style={{width: '10%'}}>
                      Rank
                    </th>
                    <th>
                      Song
                    </th>
                  </tr>
                    {songs.shortTerm.map((song, i) => (
                      <tr>
                        <td>{i + 1}.</td>
                        <td><img src={song.image.url} className='image' />{song.name}</td>
                      </tr>
                    ))}
                </Table>
            }

          </div>
        </>
    );
}
