import SongTable from './SongTable';
import ArtistTable from './ArtistTable';
import GenreTable from './GenreTable';

import { useState, useEffect } from 'react';
import { Dropdown, DropdownButton } from 'react-bootstrap';
import axios from 'axios';

export default () => {

    const [songs, setSongs] = useState();
    const [artists, setArtists] = useState();
    const [genres, setGenres] = useState();

    const [settings, setSettings] = useState({
        category: "songs",
        timeframe: "shortTerm"
    });

    useEffect(() => {
            async function fetchData() {
                let data = await axios.get(process.env.REACT_APP_API_URL);
                setSongs(data.data.topSongs);
                setArtists(data.data.topArtists);
                setGenres(data.data.topGenres);



                //console.log(data.data.topGenres);
            }
            fetchData();
        },
    []);

/*
    useEffect(() => {
        console.log(settings);
    }, [settings]);
*/

    return(
        <>
          <div id='main-page-background'>
            <div id='dropdown-wrapper'>
              <DropdownButton className='dropdown' variant='secondary' size='lg' title='Category'>
                <Dropdown.Item onClick={() => setSettings({
                    category: "songs",
                    timeframe: settings.timeframe
                })}>
                  Songs
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item onClick={() => setSettings({
                    category: "artists",
                    timeframe: settings.timeframe
                })}>
                  Artists
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item onClick={() => setSettings({
                    category: "genres",
                    timeframe: settings.timeframe
                })}>
                  Genres
                </Dropdown.Item>
              </DropdownButton>
              <DropdownButton className='dropdown' variant='secondary' size='lg' title='Time period'>
                <Dropdown.Item onClick={() => setSettings({
                    category: settings.category,
                    timeframe: "shortTerm"
                })}>
                  Last month
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item onClick={() => setSettings({
                    category: settings.category,
                    timeframe: "mediumTerm"
                })}>
                  Last 6 months
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item onClick={() => setSettings({
                    category: settings.category,
                    timeframe: "longTerm"
                })}>
                  All time
                </Dropdown.Item>
              </DropdownButton>
            </div>
            {/* DEFAULT TABLE
              {songs != null && 
                  <SongTable songs={songs.shortTerm} />
              }
            */}
            {artists != null && 
                <ArtistTable artists={artists.shortTerm} />
            }
          </div>
        </>
    );
}
