import { Button } from 'react-bootstrap';

export default () => {
    console.log(process.env.REACT_APP_BACKEND_URL);

    return(
        <div className="background" style={{    
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
        }}>
          <div id='wrapper'>
            <h1>Statify</h1>
            <h4>Welcome to Statify! Here you can view your top most played songs, artists, and genres from various periods of time. Think of it as a Spotify Wrapped that you can access at any time during the year. Your listening data remains 100% private: none of it is saved. Login to get started!</h4>
            <a class="btn btn-secondary" href={process.env.REACT_APP_BACKEND_URL} role="button" style={{width: '10%', fontSize: '200%'}}>
              Login
            </a>
          </div>
          <div id='footer'>
            <p>Made by Matthew Lin, Â© 2022. DISCLAIMER: This project is not affiliated with Spotify in any way.</p>
          </div>
        </div>
    );
}
