import { Table, Modal } from 'react-bootstrap';
import QuestionIcon from '../images/question-icon.png';

export default ({songs}) => {

    const displayList = (items) => {
        let list = "";
        for (let i = 0; i < items.length; i++) {
            list += items[i];
            if (i != items.length - 1) {
                list += ", ";
            }
        }
        if (list == "") {
            list = "Sorry, Spotify doesn't store that data currently :("
        }
        return list;
    }

    return(
      <>
        <Modal>
          <Modal.Header closeButton>
            <Modal.Title>Relative Popularity</Modal.Title>
          </Modal.Header>
          <Modal.Body>This column of the table displays the popularity of each song across Spotify's entire userbase relative to the other songs shown. This is different from the song's ranking, which is based on how much you have listened to each song. For example, a relative popularity of 1 indicates that song is the most popular song in the table across the Spotify userbase, even though it might have a different ranking. </Modal.Body>
        </Modal>
        <Table variant='dark' className='table' striped bordered>
          <tr>
            <th id='padding-column'></th>
            <th id='rank-column'>
              Rank
            </th>
            <th id='padding-column'></th>
            <th id='song-column'>
              Song
            </th>
            <th id='artists-column'>
              Artists
            </th>
            <th id='album-column'>
              Album
            </th>
            <th id='padding-column'></th>
            <th id='popularity-column'>
              Relative popularity
              <button className='question-click'>
                <img src={QuestionIcon} className='question-icon' />
              </button>
            </th>
            <th id='padding-column'></th>
          </tr>
          {songs.map((song, i) => (
              <tr key={i}>
                <td></td>
                <td>
                    {i + 1}.
                </td>
                <td></td>
                <td>
                  <img src={song.image.url} className='image' />{song.name}
                </td>
                <td>
                    {displayList(song.artists)}
                </td>
                <td>
                    {song.album}
                </td>
                <td></td>
                <td>
                  {song.popularity}
                </td>
                <td></td>
              </tr>
          ))}
        </Table>
      </>
    );
}
