import { useState, useEffect } from 'react';
import { Dropdown, DropdownButton, Table } from 'react-bootstrap';
import axios from 'axios';

export default () => {

    const [songs, setSongs] = useState();
    const [artists, setArtists] = useState();
    const [genres, setGenres] = useState();

    const [settings, setSettings] = useState({
        category: "songs",
        timeframe: "shortTerm"
    });

    useEffect(() => {
            async function fetchData() {
                let data = await axios.get(process.env.REACT_APP_API_URL);
                setSongs(data.data.topSongs);
                setArtists(data.data.topArtists);
                setGenres(data.data.topGenres);



                //console.log(data.data.topArtists);
            }
            fetchData();
        },
    []);

    useEffect(() => {
        console.log(settings);
    }, [settings]);

    const displayArtists = (artists) => {
        let list = "";
        for (let i = 0; i < artists.length; i++) {
            list += artists[i];
            if (i != artists.length - 1) {
                list += ", ";
            }
        }
        return list;
    }

    return(
        <>
          <div id='main-page-background'>
            <div id='dropdown-wrapper'>
              <DropdownButton className='dropdown' variant='secondary' size='lg' title='Category'>
                <Dropdown.Item onClick={() => setSettings({
                    category: "songs",
                    timeframe: settings.timeframe
                })}>
                  Songs
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item onClick={() => setSettings({
                    category: "artists",
                    timeframe: settings.timeframe
                })}>
                  Artists
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item onClick={() => setSettings({
                    category: "genres",
                    timeframe: settings.timeframe
                })}>
                  Genres
                </Dropdown.Item>
              </DropdownButton>
              <DropdownButton className='dropdown' variant='secondary' size='lg' title='Time period'>
                <Dropdown.Item onClick={() => setSettings({
                    display: settings.display,
                    timeframe: "shortTerm"
                })}>
                  Last month
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item onClick={() => setSettings({
                    display: settings.display,
                    timeframe: "mediumTerm"
                })}>
                  Last 6 months
                </Dropdown.Item>
                <Dropdown.Divider />
                <Dropdown.Item onClick={() => setSettings({
                    display: settings.display,
                    timeframe: "longTerm"
                })}>
                  All time
                </Dropdown.Item>
              </DropdownButton>
            </div>
            {songs != null && 
                <Table variant='dark' className='table' striped bordered>
                  <tr>
                    <th id='padding-column'></th>
                    <th id='rank-column'>
                      Rank
                    </th>
                    <th id='padding-column'></th>
                    <th id='song-column'>
                      Song
                    </th>
                    <th id='artists-column'>
                      Artists
                    </th>
                    <th id='album-column'>
                      Album
                    </th>
                    <th id='padding-column'></th>
                  </tr>
                  {songs.longTerm.map((song, i) => (
                      <tr>
                      <td></td>
                      <td>
                          {i + 1}.
                      </td>
                      <td></td>
                      <td>
                          <img src={song.image.url} className='image' />{song.name}
                      </td>
                      <td>
                          {displayArtists(song.artists)}
                      </td>
                      <td>
                          {song.album}
                      </td>
                      <td></td>
                      </tr>
                  ))}
                </Table>
            }

          </div>
        </>
    );
}
